import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { SkillService } from '../../services/skill.service';
import { Skill } from '../../models/skill.model';

@Component({
  selector: 'app-skills',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="skills-container">
      <div class="container">
        <div class="page-header">
          <h1 class="page-title">Technical Skills</h1>
          <p class="page-subtitle">My expertise across different technologies and tools</p>
        </div>

        <!-- Filter Section -->
        <div class="filter-section">
          <div class="filter-tabs">
            <button 
              *ngFor="let skillType of skillTypes" 
              [class.active]="selectedSkillType === skillType"
              (click)="filterByType(skillType)"
              class="filter-tab">
              {{ skillType }}
            </button>
            <button 
              [class.active]="selectedSkillType === 'All'"
              (click)="filterByType('All')"
              class="filter-tab">
              All Skills
            </button>
          </div>
        </div>

        <!-- Skills Grid -->
        <div class="skills-grid" *ngIf="filteredSkills.length > 0; else noSkills">
          <div class="skill-card" *ngFor="let skill of filteredSkills">
            <div class="skill-header">
              <h3 class="skill-name">{{ skill.name }}</h3>
              <span class="skill-level">{{ skill.proficiencyLevel }}%</span>
            </div>
            <div class="skill-bar">
              <div class="skill-progress" [style.width.%]="skill.proficiencyLevel"></div>
            </div>
            <div class="skill-meta">
              <span class="skill-type">{{ skill.skillType }}</span>
            </div>
          </div>
        </div>

        <ng-template #noSkills>
          <div class="no-skills">
            <div class="no-skills-content">
              <h3>No skills found</h3>
              <p>Skills will appear here once they're added</p>
            </div>
          </div>
        </ng-template>

        <!-- Loading State -->
        <div class="loading" *ngIf="isLoading">
          <div class="loading-spinner"></div>
          <p>Loading skills...</p>
        </div>

        <!-- Skills Summary -->
        <div class="skills-summary" *ngIf="skills.length > 0 && !isLoading">
          <h2>Skills Summary</h2>
          <div class="summary-grid">
            <div class="summary-item" *ngFor="let skillType of skillTypes">
              <div class="summary-header">
                <h3>{{ skillType }}</h3>
                <span class="skill-count">{{ getSkillsByType(skillType).length }} skills</span>
              </div>
              <div class="summary-skills">
                <span class="skill-tag" *ngFor="let skill of getSkillsByType(skillType)">
                  {{ skill.name }}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .skills-container {
      min-height: 100vh;
      padding: 40px 0;
      background: #f9fafb;
    }

    .container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 0 20px;
    }

    .page-header {
      text-align: center;
      margin-bottom: 50px;
    }

    .page-title {
      font-size: 3rem;
      font-weight: 700;
      color: #1f2937;
      margin-bottom: 15px;
    }

    .page-subtitle {
      font-size: 1.25rem;
      color: #6b7280;
      max-width: 600px;
      margin: 0 auto;
    }

    .filter-section {
      margin-bottom: 40px;
    }

    .filter-tabs {
      display: flex;
      justify-content: center;
      flex-wrap: wrap;
      gap: 10px;
    }

    .filter-tab {
      padding: 10px 20px;
      border: 2px solid #e5e7eb;
      background: white;
      border-radius: 25px;
      cursor: pointer;
      transition: all 0.3s ease;
      font-weight: 500;
      color: #374151;
    }

    .filter-tab:hover {
      border-color: #2563eb;
      color: #2563eb;
    }

    .filter-tab.active {
      background: #2563eb;
      border-color: #2563eb;
      color: white;
    }

    .skills-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 25px;
      margin-bottom: 60px;
    }

    .skill-card {
      background: white;
      padding: 25px;
      border-radius: 12px;
      box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
      transition: transform 0.3s ease;
    }

    .skill-card:hover {
      transform: translateY(-3px);
    }

    .skill-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 15px;
    }

    .skill-name {
      font-size: 1.25rem;
      font-weight: 600;
      color: #1f2937;
      margin: 0;
    }

    .skill-level {
      font-size: 1.1rem;
      font-weight: 700;
      color: #2563eb;
    }

    .skill-bar {
      height: 8px;
      background: #e5e7eb;
      border-radius: 4px;
      overflow: hidden;
      margin-bottom: 15px;
    }

    .skill-progress {
      height: 100%;
      background: linear-gradient(90deg, #2563eb, #3b82f6);
      transition: width 0.8s ease;
    }

    .skill-meta {
      text-align: right;
    }

    .skill-type {
      font-size: 0.9rem;
      color: #6b7280;
      background: #f3f4f6;
      padding: 4px 12px;
      border-radius: 12px;
    }

    .no-skills {
      text-align: center;
      padding: 80px 20px;
    }

    .no-skills-content h3 {
      font-size: 1.5rem;
      color: #374151;
      margin-bottom: 10px;
    }

    .no-skills-content p {
      color: #6b7280;
    }

    .loading {
      text-align: center;
      padding: 40px;
    }

    .loading-spinner {
      width: 40px;
      height: 40px;
      border: 4px solid #e5e7eb;
      border-top: 4px solid #2563eb;
      border-radius: 50%;
      animation: spin 1s linear infinite;
      margin: 0 auto 20px;
    }

    @keyframes spin {
